import React, { useEffect, useState } from "react";
import axios from "axios";
import {Link} from "react-router-dom";

export default function Home() {

  const [Employees,setEmployees] = useState([]);


  useEffect(() => {
    loadEmployees();
  });

  const loadEmployees = async () => {
    const result = await axios.get("http://localhost:8080/getEmployee");
    setEmployees(result.data);
  };
 
  const deleteEmployee= async (id)=>{
    await axios.delete(`http://localhost:8080/deleteEmployee/${id}`)
    loadEmployees();
  }


  return (
    <div className="container py-4">
         <table className="table border Shadow">
          <thead>
            <tr>
              <th scope="col">Employee_Id</th>
              <th scope="col">Employee_Name</th>
              <th scope="col">Employee_EmailId</th>
              <th scope="col">Employee_PhoneNo</th>
              <th scope="col">Action</th>
            </tr>
          </thead>
          <tbody>
             {
                   Employees.map((Employee,index)=>(
                    <tr>
                    <th scope="row" key={index}>{index+1}</th>
                    <td>{Employee.name}</td>
                    <td>{Employee.email}</td>
                    <td>{Employee.phoneNo}</td>
                    <td>
                      <Link className="btn btn-primary mx-2" to={`/viewEmployee/${Employee.id}`}>View</Link>
                      <Link className="btn btn-outline-primary mx-2" to={`/editEmployee/${Employee.id}`} >Edit</Link>
                      <button className="btn btn-danger mx-2" onClick={()=>deleteEmployee(Employee.id)}>Delete</button>
                    </td>
                  </tr>
                   ))
           }
          </tbody>
        </table>
      </div>
  );
}
