import axios from "axios";
import React, { useEffect, useState } from "react";
import { Link, useNavigate, useParams } from "react-router-dom";

export default function EditEmployee() {
  
  let navigate = useNavigate();

  const { id } = useParams();

  const [Employee, setEmployee] = useState({
    name: "",
    email: "",
    phoneNo:""
  });
  const { name,email,phoneNo} = Employee;

  useEffect(() => {
    loadEmployee();
  },[]);

  const onInputChange = (event) => {
    setEmployee({ ...Employee, [event.target.name]: event.target.value });
  };

  const onSubmit = async (event) => {
    event.preventDefault();
    await axios.put(`http://localhost:8080/updateEmployee/${id}`, Employee);
    navigate("/");
  };

  const loadEmployee = async () => {
    const result = await axios.get(`http://localhost:8080/getEmployee/${id}`);
    setEmployee(result.data);
  };

  return (
    <div className="text-center container">
      <div className="row">
        <div className="col-md-6 offset-md-3 border rounded shadow p-4 mt-2">
          <h2 className="text-center m-4">Edit Employee</h2>

          <form onSubmit={(e) => onSubmit(e)}>
            <div className="mb-3">
              <label htmlFor="name" className="form-label">
                Name
              </label>
              <input
                type={"text"}
                className="form-control"
                placeholder="Enter Employee_Name"
                name="name"
                value={name}
                onChange={(e) => onInputChange(e)}
              />
            </div>

            <div className="mb-3">
              <label htmlFor="email" className="form-label">
                E-Mail
              </label>
              <input
                type={"text"}
                className="form-control"
                placeholder="Enter Employee_EmailId"
                name="email"
                value={email}
                onChange={(e) => onInputChange(e)}
              />
            </div>

            <div className="mb-3">
              <label htmlFor="phoneNo" className="form-label">
                Phone_No
              </label>
              <input
                type={"text"}
                className="form-control"
                placeholder="Enter Employee_PhoneNo"
                name="phoneNo"
                value={phoneNo}
                onChange={(e) => onInputChange(e)}
              />
            </div>
            <button type="submit" className="btn btn-outline-success mx-3">
              Submit
            </button>
            <Link className="btn btn-outline-danger" to="/">
              Cancel
            </Link>
          </form>
        </div>
      </div>
    </div>
  );
}
