import { useEffect, useState } from "react";

function Time(){
    let [count,setcount]=useState(1);
    useEffect(()=>{
        console.log("Screen Rendered")
    },[count])

    

    function counting(){
       setcount((data)=>{return data+1})
    }

    return(
        <>
        <h1>I Have Rendered {count} Times</h1>
        <button onClick={counting}>Screen_change_count</button>
        </>
    );
}
export default Time;
