import { useState } from "react";

function Form() {
  // const[name,setname]=useState(""); const[age,setage]=useState(""); const[email,setemail]=useState("");
  const [input, setinputs] = useState({Phone:"91",EmailID:"@gmail.com",Country:"India",About:"I Am Java Developer"});

  function Submitfun(e) {
    e.preventDefault();
    console.log("Form Submitted!!");
    console.log("Corrent State", input);
  }

  function handler(e) {
     const name=e.target.name;
     const value=e.target.value;
    setinputs((data)=>{return {...data,[name]:value}});
  }
  return (
    <>
      <form onSubmit={Submitfun}>
        <label>
          Enter Your Name <input type="text" name="Name" onChange={handler} />
        </label>
        <br />
        <label>
          Enter Your Age <input type="text" name="Age" onChange={handler} />
        </label>
        <br />
        <label>
          Enter Your EmailID <input type="text" name="EmailID" value={input.EmailID} onChange={handler} />
        </label>
        <br />
        <label>
          Enter Your Phone <input type="text" name="Phone" value={input.Phone} onChange={handler} />
        </label>
        <br />
        <label>
          Enter Your Country{" "}
          <select name="Country" value={input.Country} onChange={handler}>
            <option value={""}>Select</option>
            <option value={"India"}>India</option>
            <option value={"Austrila"}>Austrila</option>
            <option value={"United States"}>United States</option>
          </select>
        </label>
        <br />
        <label>
          Enter Your About <textarea name="About" value={input.About} onChange={handler} />
        </label>
        <br />
        <input type="submit" value="Submit"></input>
      </form>
    </>
  );
}
export default Form;
