import React, { useState } from "react";
import Header from "./components/Header";
import Footer from "./components/Footer";
import Content from "./components/Content";
import "./App.css";

function App() {
  const [item, setItem] = useState([
    {
      id: 1,
      checked: true,
      content: "Morning",
    },
    {
      id: 2,
      checked: false,
      content: "Afternoon",
    },
    {
      id: 3,
      checked: false,
      content: "Night",
    },
  ]);
  function handleChange(id) {
    const listitem = item.map((objectData) =>
      objectData.id === id
        ? { ...objectData, checked: !objectData.checked }
        : objectData
    );
    setItem(listitem);
