import "./App.css";
import Header from "./Components/Header";
import Navbar from "./Components/Navbar";
import Home from "./Components/Home";
import NewPost from "./Components/NewPost";
import PostPage from "./Components/PostPage";
import Missing from "./Components/Missing";
import About from "./Components/About";
import Footer from "./Components/Footer";
import { Routes, Route, useNavigate} from "react-router-dom";
import { useState } from "react";
import { format } from "date-fns";
import { useEffect } from "react";

function App() {
  const [posts, setPosts] = useState([
    { id: 1, title: "First", datetime: "july 07, 2021 11:17:36 AM", body: "A" },
    {
      id: 2,
      title: "Second",
      datetime: "july 07, 2021 11:17:36 AM",
      body: "B",
    },
    { id: 3, title: "Third", datetime: "july 07, 2021 11:17:36 AM", body: "C" },
  ]);
  const [search, setSearch] = useState("");
  const [searchResults, setSearchResults] = useState([]);
  const [postBody, setpostBody] = useState("");
  const [postTitle, setpostTitle] = useState("");
  const navigate = useNavigate();
  useEffect(() => {
    const fillteredResults = posts.filter(
      (post) =>
        post.body.toLowerCase().includes(search.toLowerCase()) ||
        post.title.toLowerCase().includes(search.toLowerCase())
    );

    setSearchResults(fillteredResults.reverse());
  }, [posts, search]);

  const handleSubmit = (event) => {
    event.preventDefault();
    const id = posts.length ? posts[posts.length - 1].id + 1 : 1;
    const datetime = format(new Date(), "mmmm dd, yyyy pp");
    const newpost = { id, title: postTitle, datetime, body: postBody };
    const allposts = [...posts, newpost];
    setPosts(allposts);
    setpostTitle("");
    setpostBody("");
    navigate("/");
  };

  const handleDelete=(id)=>{
    const postList=posts.filter(post=>post.id!==id);
    setPosts(postList);
    navigate("/");
  }

  return (
    <div className="App">
      <Header title=" Social Media App" />
      <Navbar search={search} setSearch={setSearch} />

      <Routes>
        <Route path="/" element={<Home posts={searchResults} />} />
        <Route path="/post">
          <Route index element={
              <NewPost
                handleSubmit={handleSubmit}
                postTitle={postTitle}
                setpostTitle={setpostTitle}
                postBody={postBody}
                setpostBody={setpostBody}
              />
            }/>
            <Route path=":id" element={<PostPage posts={posts} handleDelete={handleDelete}/>}/>
        </Route>
        <Route path="/about" element={<About />} />
        <Route path="*" element={<Missing />} />
      </Routes>
      <Footer />
    </div>
  );
}
export default App;
