import React, { useState } from "react";
import Header from "./components/Header";
import Footer from "./components/Footer";
import Content from "./components/Content";
import "./App.css";
import Additem from "./components/Addltem";
import Searchitem from "./components/Searchitem";
function App() {
  const [items, setItem] = useState(JSON.parse(localStorage.getItem("todo_list")));
  
  const [newItem, setNewItem]=useState('');
  
  const [search, searchItem] =useState('');

  function addItem(item){
    const id=items.length? items[items.length-1].id+1:1;
    const addNewItem={id,checked:false,content:item}
    const listItems=[...items,addNewItem]
    setItem(listItems)
    localStorage.setItem("todo_list",JSON.stringify(listItems))
  }

  function handleSubmit(event){
    event.preventDefault();
    if(!newItem) return;
    addItem(newItem);
    setNewItem('');
    
  }

  function handleChange(id) {
    const listitems = items.map((objectData) =>
      objectData.id === id
        ? { ...objectData, checked: !objectData.checked }
        : objectData
    );
    setItem(listitems);
    localStorage.setItem("todo_list",JSON.stringify(listitems))
  }
  
  function handleDelete(id) {
    const listitems = items.filter((objectData) => objectData.id !== id);
    setItem(listitems);
    localStorage.setItem("todo-list",JSON.stringify(listitems))
  }

  return (
    <div className="App">
      <Header/>
      <Additem newItem={newItem}
               setNewItem={setNewItem}
               handleSubmit={handleSubmit}
      />
      <Searchitem search={search}
                  searchItem={searchItem}
      />
      <Content   items={items.filter((list)=>((list.list).toLowerCase()).includes(search.toLowerCase()))}
                 handleChange={handleChange} 
               handleDelete={handleDelete}
             
       
       />
      <Footer  length={items.length} />
    </div>
  );
}
export default App;
