import React, { useState } from "react";
const Dummy = () => {
  const [item, setItem] = useState([
    {
      id: 1,
      checked: true,
      content: "Morning",
    },
    {
      id: 2,
      checked: false,
      content: "Afternoon",
    },
    {
      id: 3,
      checked: false,
      content: "Night",
    },
  ]);
  function handlechange(id) {
    const itemlist = item.map((data) =>
      data.id === id ? { ...data, checked: !data.checked } : data
    );
    setItem(itemlist);
  }
   
  function handledelete(id) {
    const itemlist=item.filter((data)=>data.id!==id);
    setItem(itemlist);
  }

  return (
    <>
      {(item.length)}
      <ul>
        {item.map((data) => (
          <li key={data.id}>
            <input
              type="checkbox"
              checked={data.checked}
              onChange={() => handlechange(data.id)}
            />
            <label style={(data.checked)?{textDecoration: 'line-through'}:null}
            onDoubleClick={() => handlechange(data.id)}>{data.content}</label>
            <button onClick={()=>handledelete(data.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </>
  );
};
export default Dummy;
