import axios from "axios";
import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";

export default function AddEmployee() {
  const navigate = useNavigate();

  const [Employee, setEmployee] = useState({
    name: "",
    email: "",
    phoneNo:""
  });

  const { name,email,phoneNo} = Employee;

  const onInputChange = (event) => {
    setEmployee({ ...Employee, [event.target.name]: event.target.value });
  };

  const onSubmit = async (event) => {
    event.preventDefault();
    await axios.post("http://localhost:8080/postEmployee", Employee);
    navigate("/");
  };

  return (
    <div className="text-center container">
      <div className="row">
        <div className="col-md-6 offset-md-3 border rounded shadow p-5 mt-2">
          <h2 className="text-center m-2 mb-3">Register Employee</h2>

          <form onSubmit={(e) => onSubmit(e)}>
            <div className="mb-3">
              <label htmlFor="name" className="form-label">
                Name
              </label>
              <input
                type={"text"}
                className="form-control"
                placeholder="Enter Employee_Name"
                name="name"
                value={name}
                onChange={(e) => onInputChange(e)}
              />
            </div>

            <div className="mb-3">
              <label htmlFor="EmployeeName" className="form-label">
               E-mail
              </label>
              <input
                type={"text"}
                className="form-control"
                placeholder="Enter Employee_EmailID"
                name="email"
                value={email}
                onChange={(e) => onInputChange(e)}
              />
            </div>

            <div className="mb-3">
              <label htmlFor="email" className="form-label">
                PhoneNo
              </label>
              <input
                type={"text"}
                className="form-control"
                placeholder="Enter Employee_PhoneNo"
                name="phoneNo"
                value={phoneNo}
                onChange={(e) => onInputChange(e)}
              />
            </div>
            <button type="submit" className="btn btn-outline-success mx-3">
              Submit
            </button>
            <Link className="btn btn-outline-danger" to="/">
              Cancel
            </Link>
          </form>
        </div>
      </div>
    </div>
  );
}
