import React, { useState } from "react";
import {FaTrashAlt} from "react-icons/fa"
const Content = () => {
  const [item, setItem] = useState([
    {
      id: 1,
      checked: true,
      content: "Morning",
    },
    {
      id: 2,
      checked: false,
      content: "Afternoon",
    },
    {
      id: 3,
      checked: false,
      content: "Night",
    },
  ]);
  function handlechange(id) {
    const itemlist = item.map((objectData) =>
      objectData.id === id? { ...objectData, checked: !objectData.checked } : objectData
    );
    setItem(itemlist);
  }

  function handledelete(id) {
    const itemlist = item.filter((objectData) => objectData.id !== id);
    setItem(itemlist);
  }

  return (
    <main>
      {item.length ? (
        <ul className="item">
          {item.map((objectData) => (
            <li key={objectData.id}>
              <input
                type="checkbox"
                checked={objectData.checked}
                onChange={() => handlechange(objectData.id)}
              />
              <label
                style={
                  objectData.checked ? { textDecoration: "line-through" } : null
                }
                onDoubleClick={() => handlechange(objectData.id)}
              >
        {objectData.content}
              </label>
              <button onClick={() => handledelete(objectData.id)}>
                <FaTrashAlt
                  role="button" tabIndex="0"
                />
              </button>
            </li>
          ))}
        </ul>
      ) : (
        <p>Your List is Empty</p>
      )}
    </main>
  );
};
export default Content;
