import React from "react";

class bike extends React.Component{
   constructor(){
      super();
      this.state={color:"White",year:"2018"}
   }
 render(){
    return(
      <>
      <h1>My Bike {this.state.color} RoyalEnfield Purchase Year {this.state.year}</h1>
      <button onClick={()=>this.setState({color:"Black",year:"2020"})}>Change color</button>
      </>
    ); 
 }                   
}
export default bike;
