const apiRequest = async (url = "", optionObj = null, errorMsg = null) => {
  try {
    const response = await fetch(url, optionObj);
    if (!response) throw Error("Please Reload App");
  } catch (error) {
          errorMsg = error.message;
  } finally {
       return errorMsg
  }
};
export default apiRequest;
