import React from "react";

const Posts=({post})=>{
   return(
    <article className="posts">
      <h1>{post.title}</h1>
      <p className="postDate">{post.datetime}</p>
      <p className="postBody">{(post.body).length <=30
       ? post.body
       : `${(post.body).slice(0,30)}...`
      }</p>

    </article>
   )

}

export default Posts;
