import axios from "axios";
import React, { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";

export default function ViewEmployee() {
  const { id } = useParams();
  const [Employee, setEmployee] = useState({
    name: "",
    email: "",
    phoneNo:""
  });

  useEffect(() => {
    loadEmployee();
  });
  const loadEmployee = async () => {
    const result = await axios.get(`http://localhost:8080/getEmployee/${id}`);
    setEmployee(result.data);
  };

  return (
    <div className="text-center container">
      <div className="row">
        <div className="col-md-6 offset-md-3 border rounded shadow p-4 mt-2">
          <h2 className="text-center m-4">Employee Details</h2>

          <div className="card">
            <div className="card-header text-success">
              Details Of Particular Employee
              <ul className="list-group list-group-flush">
                <li className="list-group-item">
                  <b>Employee_Name: {Employee.name}</b>
                </li>
                <li className="list-group-item">
                  <b>Employee_Email: {Employee.email}</b>
                </li>
                 <li className="list-group-item">
                  <b>Employee_PhoneNo: {Employee.phoneNo}</b>
                </li>
              </ul>
            </div>
          </div>
          <Link className="btn btn-primary my-2" to={"/"}>
            Back To Home
          </Link>
        </div>
      </div>
    </div>
  );
}
