import React from "react";
import {Link} from "react-r"

const Posts = ({ post }) => {
  return (
    <article className="post">
      <Link to={`post/${post.id}`}>
      <h1>{post.title}</h1>
      <p className="postDate">{post.datetime}</p></Link>
      <p className="postbody">{(post.body).length<=30
       ? post.body
       :`${(post.body).slice(0,30)}...`}</p>
    </article>
  );
};

export default Posts;
