// npm i react-icons --save
// npx json-server -p 3500 -w -data/db.json
import React, { useState, useEffect } from "react";
import Header from "./components/Header";
import Footer from "./components/Footer";
import Content from "./components/Content";
import "./App.css";
import Additem from "./components/Addltem";
import Searchitem from "./components/Searchitem";
import apiRequest from "./components/Api-request";
function App() {
  const API_URL = "http://localhost:3500/items";
  const [items, setItem] = useState([]);
  const [newItem, setNewItem] = useState("");
  const [search, searchItem] = useState("");
  const [feachError, setFeachError] = useState(null);
  const [isLoading, setisLoading] = useState(true);
  useEffect(() => {
    const fetchItem = async () => {
      try {
        const response = await fetch(API_URL);
        if (!response.ok) throw Error("Data Not Recevied");
        const listItems = await response.json();
        setItem(listItems);
        setFeachError(null);
      } catch (error) {
        setFeachError(error.message);
      } finally {
        setisLoading(false);
      }
    };

    setTimeout(() => {
      (async () => fetchItem())();
    }, 2000);
  }, []);

  const addItem = async (data) => {
    const id = items.length ? items[items.length - 1].id + 1 : 1;
    const addNewItem = { id, checked: false, content: data };
    const listItems = [...items, addNewItem];
    setItem(listItems);

    const postOptions = {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(addNewItem),
    };
    const result = await apiRequest(API_URL, postOptions);
    if (result) setFeachError(result);
  };

  function handleSubmit(event) {
    event.preventDefault();
    if (!newItem) return;
    addItem(newItem);
    setNewItem("");
  }

  const handleChange = async (id) => {
    const listitems = items.map((objectData) =>
      objectData.id === id
        ? { ...objectData, checked: !objectData.checked }
        : objectData
    );
    setItem(listitems);

    const myItem = listitems.filter((item) => item.id === id)
    const updateOption = {
      method: "PATCH",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ checked: myItem[0].checked }),
    };
    const apiUrl=`${API_URL}/${id}`
    const result = await apiRequest(apiUrl, updateOption);
    if (result) setFeachError(result);
  };

  const handleDelete=async(id)=> {
    const listitems = items.filter((objectData) => objectData.id !== id);
    setItem(listitems);
    const myItem = listitems.filter((item) => item.id === id)
    const DeOption = {
      method: "DELETE",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ checked: myItem[0].checked }),
    };
    const apiUrl=`${API_URL}/${id}`
    const result = await apiRequest(apiUrl, updateOption);
    if (result) setFeachError(result);
  }

  return (
    <div className="App">
      <Header />
      <Additem
        newItem={newItem}
        setNewItem={setNewItem}
        handleSubmit={handleSubmit}
      />
      <Searchitem search={search} searchItem={searchItem} />

      <main>
        {feachError && <p>{`Error: ${feachError}`}</p>}
        {isLoading && <p>Data Is Loading...</p>}
        {!isLoading && !feachError && (
          <Content
            items={items.filter((item) =>
              item.content.toLowerCase().includes(search.toLowerCase())
            )}
            handleChange={handleChange}
            handleDelete={handleDelete}
          />
        )}
      </main>
      <Footer length={items.length} />
    </div>
  );
}
export default App;
