import { useEffect, useState } from "react";

function Time() {
  let [count, setcount] = useState(0);
  useEffect(() => {
    console.log("Screen Rendered");
    // check();
    setTimeout(()=>{
         setcount((data)=>{return data+1})
    },1000)
}, [count]);

  function counting() {
    setcount((data) => {
      return data + 1;
    });
  }
  function check(){
    if(count>=10){
        setcount(1)
    }
  }

  return (
    <>
      <h1>I Have Rendered {count} Times</h1>
      <button onClick={counting}>Screen_change_count</button>
    </>
  );
}
export default Time;
