import React from "react";

class bike extends React.Component{
   constructor(){
      super();
      this.state={color:"Orange"}
   }
 render(){
    return(
      <>
      <h1>I Am {this.state.color} RoyalEnfield</h1>
      <button onClick={()=>this.setState={color:"yellow"}}>Change color</button>
      </>
    ); 
 }
}
export default bike;
