import Header from "./components/Header";
import Footer from "./components/Footer";
import Nav from "./components/Nav";
import Home from "./components/Home";
import NewPost from "./components/NewPost";
import PostPage from "./components/PostPage";
import About from "./components/About";
import Missing from "./components/Missing";
import "./App.css";
import { Link, Route, Routes } from "react-router-dom";
import { useEffect, useState } from "react";
import { format } from "date-fns";

function App() {
  const [search, setSearch] = useState("");
  const [searchResults, setSearchResults] = useState([]);
  const [postTitle, setPostTitle] = useState([]);
  const [postBody, setPostBody] = useState([]);
  
  const [posts, setPosts] = useState([
    // {
    //   id: 1,
    //   title: "My First Post",
    //   datetime: "july 01, 2023 11:17:36 Am",
    //   body: "I Attended Marriage Event",
    // },
    // {
    //   id: 2,
    //   title: "My Second Post",
    //   datetime: "Aug 16, 2023 09:17:36 Am",
    //   body: "I Book My Ticket In RedBus App",
    // }
  ]);

  useEffect(() => {
    const fillteredResults = posts.filter((post) => {
      post.body.toLoweCase().includes(search.toLowerCase()) ||
        post.title.toLowerCase().includes(search.toLowerCase());
      setSearchResults(fillteredResults.reverse());
    });
  }, [posts, search]);

  const handleSubmit = (e) => {
    e.preventDefault();
    const id = posts.length ? posts[posts.length - 1].id + 1 : 1;
    const datetime = format(new Date(), "MMMM dd, yyyy pp");
    const newPost = { id, title: postTitle, datetime, body: postBody };
    const allPost = [...posts, newPost];
    setPosts(allPost);
    setPostTitle("");
    setPostBody("");
  };

  return (
    <div className="App">
      <Header title="Social Media" />
      <Nav search={search} setSearch={setSearch} />
      <Routes>
        <Route path="/" element={<Home posts={searchResults} />} />
        <Route
          path="/post">
          <Route index element={
            <NewPost
              handleSubmit={handleSubmit}
              postTitle={postTitle}
              setPostTitle={setPostTitle}
              postBody={postBody}
              setPostBody={setPostBody}
            />}
        /><Route path="/postpage" element={<PostPage />} /></Route>
        <Route path="/about" element={<About />} />
        <Route path="*" element={<Missing />} />
      </Routes>

      <Footer />
    </div>
  );
}

export default App;
