import { useState } from "react";
function Scooty() {
  // let [color, setstate] = useState("Green");
  // let [brand, setbrand]=useState("Activa");
  // let [rate, setrate]=useState("80,000");
  // let [year, setyear]=useState("2017");

  let [scooter, setdetail] = useState({
    color: "Red",
    brand: "Activa",
    rate: "60,000",
    year: 2014,
  });
  function Update(){
       setdetail(priviousState =>{return{...priviousState,color:"Blue",brand:"Honda"}})
  }
  return (
    <>
      <h1>My Scooter</h1>
      <p>Color : {scooter.color} <button onClick={Changecolor}>Change color</button></p>
      <p>Brand : {scooter.brand} <button onClick={Changecolor}>Change Brand</button></p>
      <p>Rate : {scooter.rate}</p>
      <p>Year : {scooter.year}</p>
      
    </>
  );
}
export default Scooty;
