import React, { useState } from "react";
import Header from "./components/Header";
import Footer from "./components/Footer";
import Content from "./components/Content";
import "./App.css";
import Additem from "./components/Addltem";

function App() {
  const [items, setItem] = useState([
    {
      id: 1,
      checked: true,
      content: "Morning",
    },
    {
      id: 2,
      checked: false,
      content: "Afternoon",
    },
    {
      id: 3,
      checked: false,
      content: "Night",
    },
  ]);
  const {newItem, setNewItem}=useState('')
  
  
  function addList(item){
    const id=items.length? items[items.length-1].id+1:1;
    const addNewItem={id,checked:false,content:item}
    const listItems=[...item,addNewItem]
    setItem(listItems)
  }

  function handleSubmit(event){
    event.preventDefault();
    if(!newItem) return;
    setNewItem('');
    addList(newItem);
    
  }

  function handleChange(id) {
    const listitems = item.map((objectData) =>
      objectData.id === id
        ? { ...objectData, checked: !objectData.checked }
        : objectData
    );
    setItem(listitems);
    localStorage.setItem("todo_list",JSON.stringify(listitems))
  }
  
  function handleDelete(id) {
    const listitems = item.filter((objectData) => objectData.id !== id);
    setItem(listitems);
    localStorage.setItem("todo-list",JSON.stringify(listitems))
  }

  return (
    <div className="App">
      <Header/>
      <Additem/>
      <Additem newItem={newItem}
               setNewItem={seNewItem}
               handleSubmit={handleSubmit}
      />
      <Content handleChange={handleChange} 
               handleDelete={handleDelete}
               item={items}
       
       />
      <Footer  length={items.length} />
    </div>
  );
}
export default App;
